aqd:query-lines.aqd:query-line:
(
({sustainab*} OR {conserv*} OR {restor*})
W/3 ({ecosyst*}
OR {terrestrial}
OR {freshwater}
OR {inland}
OR {*forest*}
OR {environment*}
OR {soil}
OR {land}
)
)
AND NOT ({marine} OR {reef})

((({degrodat*} OR {degrad*} OR {fragmentat*}) W/3 ({habitat} OR
{land} OR {woodland} OR {forest} ))
OR {red list index}
)
AND NOT ({marine} OR {reef})

(
({ecosystem} W/3 {biodiversity})
OR {species diversity}
)
AND NOT ({marine} OR {reef})
